script:
    // --- Helper Functions (Common) ---
    function generateVectorId() {
        return Math.floor(1000000000 + Math.random() * 9000000000);
    }

    function getCurrentDate() {
        const date = new Date();
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return date.toLocaleDateString('en-US', options);
    }

    // --- Contributor Fields Logic (Common, parameterized) ---
    function updateContributorFields(type) { // type can be 'mobile' or 'computer'
        const contributorSelect = document.getElementById(`contributor-${type}`);
        const urlInput = document.getElementById(`contributor-url-${type}`);
        const customNameBox = document.getElementById(`custom-contributor-name-box-${type}`);
        const customNameInput = document.getElementById(`custom-contributor-name-${type}`);

        const predefinedUrls = {
            "Yamin Ahmed": "https://designlabofficial.blogspot.com/p/yamin.html",
            "Nafich Shikdar": "https://designlabofficial.blogspot.com/p/nafich-shikdar.html",
            "Muhammad Shohan": "https://designlabofficial.blogspot.com/p/muhammad-shohan.html",
            "Mushire Elahi Tanzeem": "https://designlabofficial.blogspot.com/p/mushire-elahi-tanzeem.html",
        };

        const selectedValue = contributorSelect.value;

        if (selectedValue === "Custom") {
            customNameBox.style.display = "block";
            customNameInput.value = "";
            customNameInput.required = true;
            urlInput.value = "";
            urlInput.readOnly = false;
            urlInput.placeholder = "Enter Contributor URL";
        } else {
            customNameBox.style.display = "none";
            customNameInput.required = false;
            urlInput.value = predefinedUrls[selectedValue] || "";
            urlInput.readOnly = true;
            urlInput.placeholder = "";
        }
    }

    // --- Format Change Handler (Common, parameterized) ---
    function handleFormatChange(type) {
        const mainFormatSelect = document.getElementById(`formats-${type}`);
        const fontTypeBox = document.getElementById(`font-type-box-${type}`);
        const fontTypeSelect = document.getElementById(`font-type-${type}`);
        const softwareWrapper = document.getElementById(`software-field-wrapper-${type}`);
        const softwareSelect = document.getElementById(`software-${type}`);
        const resolutionWrapper = document.getElementById(`resolution-field-wrapper-${type}`);
        const resolutionSelect = document.getElementById(`resolution-${type}`);
        const categoriesSelect = document.getElementById(`categories-${type}`);

        if (mainFormatSelect.value === 'Fonts') {
            fontTypeBox.style.display = 'block';
            if (fontTypeSelect) fontTypeSelect.required = true;

            if (softwareWrapper) softwareWrapper.style.display = 'none';
            if (softwareSelect) softwareSelect.required = false;

            if (resolutionWrapper) resolutionWrapper.style.display = 'none';
            if (resolutionSelect) resolutionSelect.required = false;

            // Automatically select "Fonts" in categories if available
            if (categoriesSelect) {
                const fontsOption = Array.from(categoriesSelect.options).find(opt => opt.value === 'Fonts');
                if (fontsOption) {
                    categoriesSelect.value = 'Fonts';
                }
            }

        } else {
            fontTypeBox.style.display = 'none';
            if (fontTypeSelect) fontTypeSelect.required = false;

            if (softwareWrapper) softwareWrapper.style.display = 'block';
            if (softwareSelect) softwareSelect.required = true;

            if (resolutionWrapper) resolutionWrapper.style.display = 'block';
            if (resolutionSelect) resolutionSelect.required = true;

             // If not Fonts, and category was Fonts, reset it or let user choose
            if (categoriesSelect && categoriesSelect.value === 'Fonts') {
                // Find the first non-disabled, non-hidden, non-Fonts option
                const defaultOption = Array.from(categoriesSelect.options).find(opt => !opt.disabled && !opt.hidden && opt.value !== 'Fonts');
                if (defaultOption) {
                     categoriesSelect.value = defaultOption.value;
                } else {
                    // Or reset to the placeholder if no other suitable option
                    const placeholderOption = Array.from(categoriesSelect.options).find(opt => opt.disabled && opt.hidden);
                    if (placeholderOption) categoriesSelect.value = placeholderOption.value;
                }
            }
        }
        autoPopulateFields(type); // Update tags/description based on format change
    }


    // --- Auto-population Logic (Common, parameterized) ---
    function autoPopulateFields(type) {
        const postTitleInput = document.getElementById(`post-title-${type}`);
        const postTitle = postTitleInput.value;
        const licenseSelect = document.getElementById(`license-${type}`);
        const licenseValue = licenseSelect.value;
        const mainFormatSelect = document.getElementById(`formats-${type}`);
        const categoriesSelect = document.getElementById(`categories-${type}`);
        const categoryValue = categoriesSelect.value;

        let actualFormatValue = mainFormatSelect.value;
        if (actualFormatValue === 'Fonts') {
            const fontTypeSelect = document.getElementById(`font-type-${type}`);
            actualFormatValue = fontTypeSelect ? fontTypeSelect.value : '';
        }


        const relatedTagsInput = document.getElementById(`related-tags-${type}`);
        const searchDescriptionInput = document.getElementById(`search-description-${type}`);

        if (postTitle.trim() !== '') {
            const titleTagsArray = postTitle.toLowerCase().replace(/-/g, ' ').replace(/[^a-z0-9\s]/gi, '').split(/\s+/).filter(tag => tag && tag.length > 0);
            let defaultDesignTags = ["graphic design", "template", "creative", "design asset", "downloadable", "digital art"];

            if (type === 'mobile') {
                defaultDesignTags.push("mobile design");
            } else if (type === 'computer') {
                defaultDesignTags.push("computer design", "vector", "raster", "source file");
            }

            if (actualFormatValue && actualFormatValue !== "") {
                defaultDesignTags.push(actualFormatValue.toLowerCase());
                if (mainFormatSelect.value === 'Fonts') {
                    defaultDesignTags.push("font");
                    defaultDesignTags.push(actualFormatValue.toLowerCase() + " font");
                } else {
                    defaultDesignTags.push(actualFormatValue.toLowerCase() + " file");
                }
            }
            defaultDesignTags.push(licenseValue.toLowerCase());
            if (categoryValue && categoryValue !== "") {
                 defaultDesignTags.push(categoryValue.toLowerCase());
            }

            const combinedTagsArray = [...new Set([...titleTagsArray, ...defaultDesignTags])];
            const tags = combinedTagsArray.join(', ');

            if (relatedTagsInput.value.trim() === '' || relatedTagsInput.getAttribute('data-auto-filled') === 'true') {
                 relatedTagsInput.value = tags;
                 relatedTagsInput.setAttribute('data-auto-filled', 'true');
            }

            let prefix = "Download the ";
            if (licenseValue.toLowerCase() === "free") prefix += "free ";
            else if (licenseValue.toLowerCase() === "premium") prefix += "premium ";

            let formatText = (actualFormatValue && actualFormatValue !== "") ? actualFormatValue + " " : "";
            let categoryText = (categoryValue && categoryValue !== "" && categoryValue !== "Fonts") ? " for " + categoryValue.toLowerCase() : (categoryValue === "Fonts" ? " font" : "");
            if (mainFormatSelect.value === 'Fonts' && categoryValue === 'Fonts') categoryText = " font"; // Ensure "font for fonts" doesn't happen

            let designTypeSpecificText = type === 'mobile' ? "enhancing your projects" : "your graphic design projects";
            let fileOrFontText = mainFormatSelect.value === 'Fonts' ? "font" : "file";


            const description = `${prefix}${postTitle} ${formatText}${fileOrFontText}${categoryText}, perfect for ${designTypeSpecificText} with professional and eye-catching designs. Get your ${licenseValue.toLowerCase()} ${actualFormatValue && actualFormatValue !== "" ? actualFormatValue.toLowerCase() : 'design'} ${fileOrFontText} today!`;

            const placeholderMatches = searchDescriptionInput.placeholder.includes("thumbnail") && (searchDescriptionInput.value.startsWith("Download the free ") || searchDescriptionInput.value.startsWith("Download the premium "));
            const autoGeneratedPatternMatches = searchDescriptionInput.value.startsWith("Download the free ") || searchDescriptionInput.value.startsWith("Download the premium ");

            if (searchDescriptionInput.value.trim() === '' || searchDescriptionInput.getAttribute('data-auto-filled') === 'true' || placeholderMatches || autoGeneratedPatternMatches) {
                searchDescriptionInput.value = description;
                searchDescriptionInput.setAttribute('data-auto-filled', 'true');
            }
        } else {
            if (relatedTagsInput.getAttribute('data-auto-filled') === 'true') {
                relatedTagsInput.value = '';
                relatedTagsInput.removeAttribute('data-auto-filled');
            }
            if (searchDescriptionInput.getAttribute('data-auto-filled') === 'true') {
                searchDescriptionInput.value = '';
                searchDescriptionInput.removeAttribute('data-auto-filled');
            }
        }
    }

    // --- License Change Handler (Common, parameterized) ---
    function handleLicenseChange(type) {
        const licenseField = document.getElementById(`license-${type}`);
        const priceBox = document.getElementById(`price-box-${type}`);
        const downloadLinkBox = document.getElementById(`download-link-box-${type}`);
        const downloadLinkInput = document.getElementById(`download-link-${type}`);

        if (licenseField.value === 'Free') {
            priceBox.style.display = 'none';
            downloadLinkBox.style.display = 'block';
            if(downloadLinkInput) downloadLinkInput.required = true;
        } else {
            priceBox.style.display = 'block';
            downloadLinkBox.style.display = 'none';
            if(downloadLinkInput) downloadLinkInput.required = false;
        }
        autoPopulateFields(type);
    }

    // --- Form Submission Handler (Common, parameterized) ---
    function handleFormSubmit(e, type) {
        e.preventDefault();

        const postTitle = document.getElementById(`post-title-${type}`).value;
        const imageUrl = document.getElementById(`image-url-${type}`).value;
        const license = document.getElementById(`license-${type}`).value;
        const price = license === 'Premium' ? document.getElementById(`price-${type}`).value + ' BDT' : '00 BDT';

        const mainFormatSelect = document.getElementById(`formats-${type}`);
        let selectedMainFormat = mainFormatSelect.value; 
        let formatsValueForDisplay = selectedMainFormat; 

        let softwareValue = "";
        let resolutionValue = "";
        let softwareDetailHTML = "";
        let resolutionDetailHTML = "";

        if (selectedMainFormat === 'Fonts') {
            const fontTypeSelect = document.getElementById(`font-type-${type}`);
            formatsValueForDisplay = fontTypeSelect ? fontTypeSelect.value : 'Font'; 
        } else {
            softwareValue = document.getElementById(`software-${type}`).value;
            resolutionValue = document.getElementById(`resolution-${type}`).value;
            softwareDetailHTML = `


Software${softwareValue}`;
            resolutionDetailHTML = `


Resolution${resolutionValue}`;
        }

        const fileSize = document.getElementById(`file-size-input-${type}`).value + ' ' + document.getElementById(`file-size-unit-${type}`).value;
        const contributorSelectValue = document.getElementById(`contributor-${type}`).value;
        let contributor = contributorSelectValue;
        if (contributorSelectValue === "Custom") {
            contributor = document.getElementById(`custom-contributor-name-${type}`).value;
        }
        const contributorUrl = document.getElementById(`contributor-url-${type}`).value;
        const categories = document.getElementById(`categories-${type}`).value;
        const uploadDate = document.getElementById(`upload-date-${type}`).value;
        const relatedTags = document.getElementById(`related-tags-${type}`).value;
        const searchDescription = document.getElementById(`search-description-${type}`).value;
        const downloadLink = license === 'Free' ? document.getElementById(`download-link-${type}`).value : '';

        const vectorId = generateVectorId();
        let buttonCode;

        if (license === 'Free') {
            buttonCode = `Free Download`;
        } else {
            buttonCode = `Buy Now!`;
        }

        let specificContent = "";
        const commonFontContent = `


Welcome to our website, where you can access both free and premium TTF, OTF, WOFF, EOT (font files), and SVG (vector graphics) files for your graphic design projects.

