            `;
            }
        }


        const generatedCode = `




${relatedTags}









            ${softwareDetailHTML}
            ${resolutionDetailHTML}



Price${price}



Size${fileSize}



Contributor${contributor}



Category${categories}



Format${formatsValueForDisplay}



Date${uploadDate}



License${license}



Vector ID${vectorId}



${buttonCode}


${specificContent}


${relatedTags}
`;

        document.getElementById('outputCode').innerText = generatedCode.trim();
        document.getElementById('generatedCodeSection').style.display = 'block';
        document.getElementById('copyButton').style.display = 'inline-block';
    }


    // --- Initial Setup Function ---
    function initializeForm(type) {
        document.getElementById(`upload-date-${type}`).value = getCurrentDate();
        updateContributorFields(type);
        document.getElementById(`contributor-${type}`).addEventListener('change', () => updateContributorFields(type));

        const licenseField = document.getElementById(`license-${type}`);
        licenseField.addEventListener('change', () => handleLicenseChange(type));
        handleLicenseChange(type); 

        const formatsField = document.getElementById(`formats-${type}`);
        formatsField.addEventListener('change', () => handleFormatChange(type));
        handleFormatChange(type); 

        document.getElementById(`post-title-${type}`).addEventListener('input', () => autoPopulateFields(type));
        document.getElementById(`categories-${type}`).addEventListener('change', () => autoPopulateFields(type));
        const fontTypeSelect = document.getElementById(`font-type-${type}`);
        if(fontTypeSelect) fontTypeSelect.addEventListener('change', () => autoPopulateFields(type));

        document.getElementById(`related-tags-${type}`).addEventListener('input', function() { this.removeAttribute('data-auto-filled'); });
        document.getElementById(`search-description-${type}`).addEventListener('input', function() { this.removeAttribute('data-auto-filled'); });

        document.getElementById(`postForm${type.charAt(0).toUpperCase() + type.slice(1)}`).addEventListener('submit', (e) => handleFormSubmit(e, type));
    }


    // --- Main Selector Logic ---
    document.getElementById('file-type-selector').addEventListener('change', function() {
        const selectedType = this.value;
        const mobileForm = document.getElementById('mobile-form-section');
        const computerForm = document.getElementById('computer-form-section');
        const generatedCodeSection = document.getElementById('generatedCodeSection');
        const copyButton = document.getElementById('copyButton');

        mobileForm.style.display = 'none';
        computerForm.style.display = 'none';
        generatedCodeSection.style.display = 'none';
        copyButton.style.display = 'none';
        document.getElementById('outputCode').innerText = ''; 

        if (selectedType === 'mobile') {
            mobileForm.style.display = 'block';
            initializeForm('mobile');
        } else if (selectedType === 'computer') {
            computerForm.style.display = 'block';
            initializeForm('computer');
        }
    });

    // --- Copy Button (Common) ---
    document.getElementById("copyButton").addEventListener("click", function () {
        const codeElement = document.getElementById("outputCode");
        const codeText = codeElement.innerText;
        const tempTextarea = document.createElement("textarea");
        tempTextarea.value = codeText;
        document.body.appendChild(tempTextarea);
        tempTextarea.select();
        document.execCommand("copy");
        document.body.removeChild(tempTextarea);
        this.innerText = "Copy completed!";
        setTimeout(() => (this.innerText = "COPY"), 2000);
    });
